CREATE TABLE `task_comments` (
	`id` int AUTO_INCREMENT NOT NULL,
	`taskId` int NOT NULL,
	`userId` int NOT NULL,
	`content` text NOT NULL,
	`createdAt` timestamp NOT NULL DEFAULT (now()),
	`updatedAt` timestamp NOT NULL DEFAULT (now()) ON UPDATE CURRENT_TIMESTAMP,
	CONSTRAINT `task_comments_id` PRIMARY KEY(`id`)
);

ALTER TABLE `tasks` ADD `recurrencePattern` enum('none','daily','weekly','monthly') DEFAULT 'none' NOT NULL;
ALTER TABLE `tasks` ADD `recurrenceEndDate` timestamp;
ALTER TABLE `tasks` ADD `parentTaskId` int;

